    isVideoEnabled: false,
    photoURL: 'https://www.shareicon.net/data/512x512/2016/05/29/772559_user_512x512.png',
    name: 'Ivan',
    role: 'student',
    status: 'do_not_disturb'
  }
]

const boardTabs: ITabProps[] = [
  {
    title: 'Whiteboard', 
    active: true, 
    closeButton: true
  }
]

const Container = styled.div`
  display: flex; 
  justify-content: space-between; 
  height: 100%; 
`

const App: React.FC = () => {

  return (
    <Container>
      <Sidebar
        users={users}
      />
      <Board 
        tabs={boardTabs}
      /> 
      <Chat
        messages={messages}
        tabs={tabs}
      />
    </Container>
  )
}

export default App;
